import React, { useState } from 'react';
import styles from './cart.module.css';
import CartItem from "../../components/cart-item/CartItem";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import Error from "../../components/feedback/error/Error";
import RazorpayPayment from "../../components/razorpay-payment/RazorpayPayment"; // Import RazorpayPayment

const Cart = ({ cart, cartCount, updateQuantity }) => {
  const dispatch = useDispatch();
  const [error, setError] = useState('');

  const getTotal = () => {
    let total = 0;
    for (const cartElement of cart) {
      total += cartElement.price * cartElement.quantity;
    }
    return total;
  };

  const handlePaymentSuccess = (paymentId) => {
    console.log("Payment Successful. Payment ID: ", paymentId);
    // Perform any additional actions you need after successful payment
  };

  const bookingDetails = {
    salonName: "Salon XYZ", // Example data
    service: "Haircut",
    name: "John Doe",
  };

  if (cart && !cart.length)
    return (
      <div className={styles['wrapper']}>
        <div className={'heading'}>
          <h1>Shopping Cart</h1>
        </div>
        <div className={styles['no-items']}>Your cart is empty</div>
        <Link to={'/products'} className={'btn1'} style={{ width: '150px', padding: '10px' }}>
          Start Shopping
        </Link>
      </div>
    );

  return (
    <div className={styles['wrapper']}>
      {error && <Error error={error} setError={setError} />}
      <div className={'heading'}>
        <h1>Shopping Cart</h1>
      </div>
      <div className={styles['products-wrapper']}>
        {cart.map((product, i) => (
          <CartItem product={product} updateQuantity={updateQuantity} key={i} />
        ))}
      </div>
      <div className={styles['total-wrapper']}>
        <div className={styles['total-text']}>Total ({cartCount} Items):</div>
        <div className={styles['total-amount']}>â‚¹{getTotal().toFixed(2)} Rs</div>
      </div>

      {/* Integrating RazorpayPayment component for payment */}
      <RazorpayPayment 
        amount={getTotal()} 
        bookingDetails={bookingDetails} 
        onPaymentSuccess={handlePaymentSuccess} 
      />
    </div>
  );
};

export default Cart;
