import React, { useState } from 'react';
import styles from './cart.module.css';
import CartItem from "../../components/cart-item/CartItem";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import Error from "../../components/feedback/error/Error";

const Cart = ({ cart, cartCount, updateQuantity }) => {
  const dispatch = useDispatch();
  const [error, setError] = useState('');

  const getTotal = () => {
    let total = 0;
    for (const cartElement of cart) {
      total += cartElement.price * cartElement.quantity;
    }
    return total;
  };

  const handlePaymentSuccess = (paymentId) => {
    console.log("Payment Successful. Payment ID: ", paymentId);
    // Perform any additional actions you need after successful payment
  };

  const handlePayment = async () => {
    // Here goes your Razorpay payment integration logic
    // For example, loading Razorpay script and handling the payment
    alert('Payment initiated');  // Temporary action for now
  };

  if (cart && !cart.length)
    return (
      <div className={styles['wrapper']}>
        <div className={'heading'}>
          <h1>Shopping Cart</h1>
        </div>
        <div className={styles['no-items']}>Your cart is empty</div>
        <Link to={'/products'} className={'btn1'} style={{ width: '150px', padding: '10px' }}>
          Start Shopping
        </Link>
      </div>
    );

  return (
    <div className={styles['wrapper']}>
      {error && <Error error={error} setError={setError} />}
      <div className={'heading'}>
        <h1>Shopping Cart</h1>
      </div>
      <div className={styles['products-wrapper']}>
        {cart.map((product, i) => (
          <CartItem product={product} updateQuantity={updateQuantity} key={i} />
        ))}
      </div>
      <div className={styles['total-wrapper']}>
        <div className={styles['total-text']}>Total ({cartCount} Items):</div>
        <div className={styles['total-amount']}>â‚¹{getTotal().toFixed(2)} Rs</div>
      </div>

      {/* Stylish Pay Now button */}
      <button
        onClick={handlePayment}
        style={{
          background: 'linear-gradient(135deg, #00C9FF 0%, #92FE9D 100%)',  // Gradient background
          color: 'white',
          fontSize: '18px',
          padding: '12px 24px',  // Improved padding
          border: 'none',
          borderRadius: '30px',  // Rounded corners
          cursor: 'pointer',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',  // Subtle shadow
          transition: 'transform 0.2s ease-in-out, background 0.2s ease-in-out',  // Smooth hover effect
          fontWeight: 'bold',
          display: 'block',
          margin: '20px auto',  // Centered button
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'scale(1.05)';  // Slightly enlarge on hover
          e.target.style.background = 'linear-gradient(135deg, #00C9FF 0%, #00FF77 100%)';  // Hover color change
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'scale(1)';
          e.target.style.background = 'linear-gradient(135deg, #00C9FF 0%, #92FE9D 100%)';  // Return to original color
        }}
      >
        Pay Now
      </button>
    </div>
  );
};

export default Cart;
